var id;
var barsbox = "<div class='bars-box ng-hide' class='addModulesBars'><div class=bars-box-justify style='top: auto; left: 726px;'><div class=bars-box-position><div class=bars-box-arrow></div><div class='cancleAdd' ng-click='cancleAdd()' class=cancleAdd></div><p class='bars-box-title ng-binding'>+添加模块</p><div class='select-modules-bars clear' id=select-modules-bars><ul class=add-module-box><li class='onClickAddText'><div class=add-text-btn></div>文字<li class=upload-button ng-click='onClickAddImage($event)'><input id=upImage type=file multiple><div class=add-image-btn></div>图片<!--<li ng-click='onClickAddVedio()'><div class=add-video-btn></div>视频<li class='onClickAddGoods'><div class=add-good-btn></div>商品<li class='onClickAddLine'><div class=add-line-gap-btn></div>分割线--></ul></div></div></div><div class=bars-box-back class='cancleAdd'></div></div>";
var emptycontent	 = "<div class='empty-content ng-hide' ng-show='diary.length == 0&amp;&amp;!model_tips'>详情目前为空</div>";
var itemstart = "<div class='click-item item ng-scope' ng-repeat='module in diary track by $index' ng-show='diary.length > 0'><div ng-switch=module.type id=module1507179294205na3cmjwug1l><div ng-switch-when=2 class='model-box ";
var itemstart_append = " ng-scope' ng-class='hover[module.id]?'hover':''' ng-mousedown='closeIn($event)' ng-mouseover='hoverIndex(module.id)' ng-mouseleave='hoverIndex(-1)'>";
var itemend = "<div class='note-ope ng-hide' ng-show='(hover[module.id]&amp;&amp;!move) || (opora[module.id]&amp;&amp;move)'><div class=add-btn ng-mousedown='model_insert($index,$event,module.id)'></div><div class=move-up ng-mousedown='model_move($index,$event,-1,module.id)' ng-show='$index>0'></div><div class='move-down ng-hide' ng-mousedown='model_move($index,$event,1,module.id)' ng-show='$index < diary.length-1'></div><div class=note-del ng-mousedown='model_delete($index)'></div></div></div><div class='choice-tips choice-bottom ng-hide' ng-mousedown='stopPro($event)' ng-show='$index == curInsertIndex'></div></div></div>";
var barsend = "<div ng-click='model_insert(-1,$event,diary[diary.length-1].id)' class='addModulesbtn'>+添加模块</div>";

function AppEdit (el){
    this._init_(el);
}

AppEdit.prototype = {
    constructor : AppEdit,
    _init_ : function(info) {
    	var id = info.el || '#editor_id';
	    var name = info.name || 'appEdit' ;
		var json = info.data || '';

		var self = this;
		var data = [];
		this.name = name;
		this.id = id;
		
		
		if(json != '') {
		   json = json.replace("\r\n", "<br>");
		   json = json.replace(/\\r\n/g, "<br>");
		   json = json.replace(/\r/ig, "").replace(/\n/ig,"");
		   
		   data=eval("("+json+")");  
		}
		
		$(id).addClass('cpc-diary-edit ng-isolate-scope ng-not-empty ng-valid ng-dirty ng-valid-parse');
		
		var html = barsbox;
		html += '<div class="editor-list"><div id="ueditor" style="padding-bottom: 10px;"><div class="diary-list">' + emptycontent;
		
		for(var i=0;i<data.length;i++){
			if(data[i].text != undefined) {
				html += this.bytext(data[i].text);
			} else if(data[i].image != undefined) {
				html += this.byimage(data[i].image);
			}
		}
		
		html += "</div>";
		html += "</div>";
		html += barsend;
		html += "</div>";
		html += "</div>";
		html += "</div>";
		html += "</div>";
		html += "</div>";
		
		$(id).append(html);
		


		$(".onClickAddText").click(function() {
			$(".click-item .choice-bottom:not(.ng-hide)").parent().parent().parent('.click-item').replaceWith(self.bytext(" "))
			
			$(".addModulesBars").addClass('ng-hide');
			$('.bars-box').addClass('ng-hide');
			
			$("body").removeClass('modal-open');
			self.init();
			self.add_after();
		});

			
		$(".addModulesbtn").click(function() {
			$("body").addClass('modal-open');
			$(this).after("<div class='mark addModulesBars' ng-show='addModulesBars'></div>");
			$('.bars-box').removeClass('ng-hide');
			$('.empty-content').addClass('ng-hide');
			$(".bars-box-justify").attr('style',"top: 16px;left: 478px;");
			
			var append = itemstart + " text-model-wrapper " + itemstart_append + "<div class='choice-tips choice-bottom' ng-mousedown='stopPro($event)' ></div>" +  itemend;
			$(".diary-list").append(append);
		});
		
		
		$(".model-box").mousedown(function() {
			$('.click-item').first().find('.note-ope').find('.move-up').addClass('move-down').removeClass('move-up');
			$('.click-item').not($('.click-item').first()).find('.note-ope').find('.move-down').addClass('move-up').removeClass('move-down');
			
			$(this).find('.note-ope').removeClass('ng-hide');
				
			$(".move-down").mousedown(function() {
				var current = $(this).parent().parent().parent().parent('.click-item');
				var next = $(current).next(".click-item");
		
				$(next).insertBefore(current);
			});
			
			$(".move-up").mousedown(function() {
				var current = $(this).parent().parent().parent().parent('.click-item');
				var prev = $(current).prev(".click-item");
				
				$(prev).insertAfter(current);
			});
		});
		
		
		$(".model-box").mouseleave(function() {
			$(this).find('.note-ope').addClass('ng-hide');
		});
		
		$(".note-del").mousedown(function() {
			if(confirm("是否删除该模块")) 
				$(this).parent().parent().parent().parent('.click-item').remove();
		});
		
		$(".bars-box-back").mousedown(function() {
			$(".click-item .choice-bottom:not(.ng-hide)").parent().parent().parent('.click-item').remove()
			$(".addModulesBars").addClass('ng-hide');
			$('.bars-box').addClass('ng-hide');
			
			$("body").removeClass('modal-open');
		});
		
		$(".textarea").addClass('ng-hide');
		$(".textarea").blur(function() {
			$(this).addClass('ng-hide');	
			$(this).prev('.moni-textarea').text($(this).val());
			$(this).next('input').val($(this).val());
		});
			
		$(".moni-textarea").click(function() {
			$(this).next('.textarea').removeClass('ng-hide');	
			$(this).next('.textarea').val($(this).text());
			$(this).next('input').val($(this).text());
		});
		
		$(".add-btn").mousedown(function() {
			var current = $(this).parent().parent().parent().parent('.click-item');
			$("body").addClass('modal-open');
			$(this).after("<div class='mark addModulesBars' ng-show='addModulesBars'></div>");
			$('.bars-box').removeClass('ng-hide');
			$('.empty-content').addClass('ng-hide');
			$(".bars-box-justify").attr('style',"top: 16px;left: 478px;");
			var append = itemstart + " text-model-wrapper " + itemstart_append + "<div class='choice-tips choice-bottom' ng-mousedown='stopPro($event)' ></div>" +  itemend;
			$(current).after(append);
		});
		
		if(data.length <= 0) {$('.empty-content').removeClass('ng-hide');}

			
		
    },
	byimage : function($val) {
		var hidden = "<input name='" + this.name + "[][image]" + "' value='" + $val + "' type='hidden'/>";
		console.log(hidden);
		return itemstart + " image-model-wrapper " + itemstart_append + "<div class='img-loading ng-hide' ng-show='diary[$index].loading'><img src=https://s.geilicdn.com/CPC/item/201709/images/loading.50c5e3e7.gif></div>" + "<img ng-src='https://si.geilicdn.com/bj-pc_diary-900906851-1507182255641-1819123877_222_222.jpg?w=222&amp;h=222' onerror='imgOnError(this)' ondragstart='return false' src='" + $val + "'>" + hidden+ itemend;
	},
	bytext : function($val) {
		var hidden = "<input name='" + this.name + "[][text]" + "' value='" + $val + "' type='hidden'/>";
		console.log(hidden);
		return itemstart + " text-model-wrapper " + itemstart_append +  "<div class='moni-textarea ng-binding' data-id=0 ng-bind-html='htmlDecode(module.text)'>" + $val + '</div>' + "<textarea id='textareaNumber0' class='textarea ng-pristine ng-valid ng-not-empty ng-touched' ng-class='txt-show-area' ng-blur='noEdit($index)' ng-mousedown='stopPro($event)' ng-model='module.text'></textarea>" + hidden + itemend;
	},
	textafter : function() {
		$(".click-item .choice-bottom:not(.ng-hide)").parent().parent().parent('.click-item').replaceWith(this.bytext($val))

		$(".addModulesBars").addClass('ng-hide');
		$('.bars-box').addClass('ng-hide');
		$("body").removeClass('modal-open');

		this.init();
		this.add_after();
	},
	uploadafter : function($val) {
		$(".click-item .choice-bottom:not(.ng-hide)").parent().parent().parent('.click-item').replaceWith(this.byimage($val))

		$(".addModulesBars").addClass('ng-hide');
		$('.bars-box').addClass('ng-hide');
		$("body").removeClass('modal-open');

		this.init();
		this.add_after();


	},
	uploadbutton : function(obj) {
		return $(".add-image-btn")
	},
	init : function() {
	 	var self = this;
		$(".model-box").unbind('mousedown');
		$(".model-box").unbind('mouseleave');
		$(".note-del").unbind('mousedown');
		
		$(".model-box").mousedown(function() {
			$(this).find('.note-ope').removeClass('ng-hide');
			
			self.init();
		});
		
		$(".model-box").mouseleave(function() {
			$(this).find('.note-ope').addClass('ng-hide');
			
			self.init();
		});
		
		$(".note-del").mousedown(function() {
			if(confirm("是否删除该模块")) 
				$(this).parent().parent().parent().parent('.click-item').remove();
			
			self.init();
		});
		
		
	},
			
	add_after : function() {
		var self = this;
		$(".add-btn").unbind('mousedown');
		$(".move-up").unbind('mousedown');
		$(".move-down").unbind('mousedown');
		$(".textarea").unbind('blur');
		$(".moni-textarea").unbind('click');
		
		$('.click-item').first().find('.note-ope').find('.move-up').addClass('move-down').removeClass('move-up');
		$('.click-item').not($('.click-item').first()).find('.note-ope').find('.move-down').addClass('move-up').removeClass('move-down');
		
		$(".move-down").mousedown(function() {
			var current = $(this).parent().parent().parent().parent('.click-item');
			var next = $(current).next(".click-item");
			$(next).insertBefore(current);
			
			
			$('.click-item').first().find('.note-ope').find('.move-up').addClass('move-down').removeClass('move-up');
			$('.click-item').not($('.click-item').first()).find('.note-ope').find('.move-down').addClass('move-up').removeClass('move-down');
			
			self.add_after();

		});
		
		$(".move-up").mousedown(function() {
			var current = $(this).parent().parent().parent().parent('.click-item');
			var prev = $(current).prev(".click-item");
			$(prev).insertAfter(current);
			
			$('.click-item').first().find('.note-ope').find('.move-up').addClass('move-down').removeClass('move-up');
			$('.click-item').not($('.click-item').first()).find('.note-ope').find('.move-down').addClass('move-up').removeClass('move-down');
			
			self.add_after();
	
		});
		
		$(".add-btn").mousedown(function() {
			var current = $(this).parent().parent().parent().parent('.click-item');
			
			$("body").addClass('modal-open');
			$(this).after("<div class='mark addModulesBars' ng-show='addModulesBars'></div>");
			$('.bars-box').removeClass('ng-hide');
			$('.empty-content').addClass('ng-hide');
			$(".bars-box-justify").attr('style',"top: 16px;left: 478px;");
			var append = itemstart + " text-model-wrapper " + itemstart_append + "<div class='choice-tips choice-bottom' ng-mousedown='stopPro($event)' ></div>" +  itemend;
			$(current).after(append);
				
			self.add_after();
	
		});
		
		$(".textarea").blur(function() {
			$(this).addClass('ng-hide');
			
			$(this).prev('.moni-textarea').text($(this).val());
			$(this).next('input').val($(this).val());
			self.add_after();	
		});
		
		$(".moni-textarea").click(function() {
			$(this).next('.textarea').removeClass('ng-hide');	
			$(this).next('.textarea').val($(this).text());
			
			self.add_after();	
		});
		
	}
}